_DateTime::_DateTime(int year, 
              int month, 
              int day, 
              int hour, 
              int minute, 
              int second, 
              int millisecond,
			  int dayOfWeek,
			  int dayOfMonth,
			  int dayOfYear,
			  long time) {
    _year = year;
	_month = month;
	_day = day;
	_hour = hour;
	_minute = minute;
	_second = second;
	_millisecond = millisecond;
	_dayOfMonth = dayOfMonth;
	_dayOfWeek = dayOfWeek;
	_dayOfYear = dayOfYear;
	_time = time;
}

int _DateTime::year() const{
    return _year;
}
		
int _DateTime::month() const {
    return _month;
}
	
int _DateTime::dayOfMonth() const {
    return _dayOfMonth;
}
		
int _DateTime::dayOfWeek() const {
	return _dayOfWeek;
}
	
int _DateTime::dayOfYear() const {
	return _dayOfYear;
}
	
int _DateTime::hour() const {
	return _hour;
}
		
int _DateTime::hourAMPM() const {
    if (_hour < 1) {
		return 12;
	} else if (_hour > 12) {
        return _hour - 12;
	} else {
		return _hour;
	}
}
	
bool _DateTime::isAM() const {
    return _hour < 12;
}

bool _DateTime::isPM() const {
    return _hour >= 12;
}
		
